//メッシュ初期化
HRESULT CSceneField::Init(CRenderer *pRenderer,int nNumBlockX, int nNumBlockZ, float fSizeBlockX, float fSizeBlockZ,D3DXVECTOR3 Pos,D3DXVECTOR3 Rot,char* filename,OBJTYPE Type)
{
	for(int cnt=0;cnt<(100*100*2);cnt++)
	{
		NormalBuff[cnt]=D3DXVECTOR3(0.0f,0.0f,0.0f);
	}
	for(int i=0;i<101*101;i++){
		HEIGHT_MAP[i] = rand()%5;
	}
	m_nNumBlockX=nNumBlockX;
	m_nNumBlockZ=nNumBlockZ;
	m_fSizeBlockX=fSizeBlockX;
	m_fSizeBlockZ=fSizeBlockZ;
	m_pDevice=pRenderer->GetDevice();
	//総頂点数
	m_nNumVertex=(nNumBlockX+1)*(nNumBlockZ+1);
	
	//ポリゴン数
	m_nNumPolygon=nNumBlockX*nNumBlockZ*2+(nNumBlockZ-1)*4;
		//インデックス総数
	m_nNumVertexIndex = (nNumBlockX+1)*(nNumBlockZ+1)+(nNumBlockZ-1)*(3+nNumBlockX);
	//地面ポリゴンの設定を初期化
	m_Pos	= Pos;
	m_Rot	= Rot;


	//ポリゴン設定
	//頂点初期化
	if(FAILED(m_pDevice->CreateVertexBuffer(sizeof(VERTEX_3D)*m_nNumVertex,
										D3DUSAGE_WRITEONLY,
										FVF_VERTEX_3D,
										D3DPOOL_MANAGED,
										&m_pD3DVtxBuff,
										NULL)))
	{
		return E_FAIL;
	}
	//インデックス初期化
	if(FAILED(m_pDevice->CreateIndexBuffer(sizeof(WORD)*m_nNumVertexIndex,
										D3DUSAGE_WRITEONLY,
										D3DFMT_INDEX16,
										D3DPOOL_MANAGED,
										&m_pD3DIndexBuff,
										NULL)))
	{
		return E_FAIL;
	}
	SetVertexPolygon(nNumBlockX,nNumBlockZ,fSizeBlockX,fSizeBlockZ);
	//テクスチャ読み込み
	D3DXCreateTextureFromFile(m_pDevice,filename,&m_pD3DTex);	
	return S_OK;

}

//終了処理
void CSceneField::Uninit()
{
	if(m_pD3DVtxBuff != NULL)
	{
		m_pD3DVtxBuff->Release();
		m_pD3DVtxBuff=NULL;
	}
	if(m_pD3DTex!=NULL)
	{
		m_pD3DTex->Release();
		m_pD3DTex=NULL;
	}
	if(m_pD3DIndexBuff!=NULL)
	{
		m_pD3DIndexBuff->Release();
		m_pD3DIndexBuff=NULL;
	}
	Release();
}
