//頂点情報のセット
void CSceneField::SetVertexPolygon(int nNumBlockX, int nNumBlockZ, float fSizeBlockX, float fSizeBlockZ)
{
	//頂点情報計算用
	float fPosX,fPosZ;
	float fTexU,fTexV;
	int nNum =0;
	VERTEX_3D *pVtx;
		//ロック
	m_pD3DVtxBuff->Lock(0,0,(void**)&pVtx,0);

	// i行t列に頂点設定
	for(int i = 0; i <= nNumBlockZ; i++)
	{
		fTexU = 0;
		fTexV = (float)i/TEX_SIZE_Z;
		for(int j = 0; j <= nNumBlockX; j++)
		{
			nNum = i * (nNumBlockX+1) + j;
			fPosX = float(-fSizeBlockX * (float)nNumBlockX/2 + (float)j*fSizeBlockX);
			fPosZ = float(fSizeBlockZ * (float)nNumBlockZ/2 - (float)i*fSizeBlockZ);
			fTexU = (float)-j/TEX_SIZE_X;
			pVtx[nNum].vtx = D3DXVECTOR3(fPosX, HEIGHT_MAP[nNum], fPosZ);
			pVtx[nNum].nor = D3DXVECTOR3(0.0f,0.0f,0.0f);	// 合計が1.0fになるようにしないとダメ
			pVtx[nNum].diffuse = D3DCOLOR_RGBA(255, 255, 255, 255);
			pVtx[nNum].tex = D3DXVECTOR2(fTexU, fTexV);
		}
	}
	//一時格納用ベクター3
	D3DXVECTOR3 NormalVec[2];
	int cnt=0;
	for(int i=0;i<=nNumBlockZ;i++)
	{
		for(int j = 0;j<=nNumBlockX;j++)
		{
			nNum = i * (nNumBlockX+1) + j;
			//まずは各面からできる法線ベクトルを作る
			if(i !=nNumBlockZ && j != 0)
			{
				NormalVec[0]=pVtx[nNum-1].vtx-pVtx[nNum].vtx;
				NormalVec[1]=pVtx[nNum+(nNumBlockX+1)].vtx-pVtx[nNum].vtx;
				D3DXVec3Cross(&NormalBuff[cnt],&NormalVec[1],&NormalVec[0]);
				D3DXVec3Normalize(&NormalBuff[cnt],&NormalBuff[cnt]);


				pVtx[nNum].nor=pVtx[nNum].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum].nor,&pVtx[nNum].nor);
				pVtx[nNum-1].nor=pVtx[nNum-1].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum-1].nor,&pVtx[nNum-1].nor);
				pVtx[nNum+(nNumBlockX+1)].nor=pVtx[nNum+(nNumBlockX+1)].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum+(nNumBlockX+1)].nor,&pVtx[nNum+(nNumBlockX+1)].nor);
				cnt++;

				NormalVec[0]=pVtx[nNum-1].vtx-pVtx[nNum-1+(nNumBlockX+1)].vtx;
				NormalVec[1]=pVtx[nNum+(nNumBlockX+1)].vtx-pVtx[nNum-1+(nNumBlockX+1)].vtx;
				D3DXVec3Cross(&NormalBuff[cnt],&NormalVec[0],&NormalVec[1]);
				D3DXVec3Normalize(&NormalBuff[cnt],&NormalBuff[cnt]);


				pVtx[nNum-1].nor=pVtx[nNum-1].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum-1].nor,&pVtx[nNum-1].nor);
				pVtx[nNum+(nNumBlockX+1)].nor=pVtx[nNum+(nNumBlockX+1)].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum+(nNumBlockX+1)].nor,&pVtx[nNum+(nNumBlockX+1)].nor);
				pVtx[nNum-1+(nNumBlockX+1)].nor=pVtx[nNum-1+(nNumBlockX+1)].nor+NormalBuff[cnt];
				D3DXVec3Normalize(&pVtx[nNum-1+(nNumBlockX+1)].nor,&pVtx[nNum-1+(nNumBlockX+1)].nor);

				cnt++;

			}
			
		}
	}

	//アンロック
	m_pD3DVtxBuff->Unlock();
	
	//インデックス設定
	WORD *pIndex;
	// xIndex行xzIndex列
	int xIndex = 0;
	int zIndex = 0;


	m_pD3DIndexBuff->Lock(0,0,(void**)&pIndex,0);	//ロック
	
	for(int i = 0; i < m_nNumVertexIndex; i++)
	{

		// 縮退ポリゴン点
		if(i == 2*(nNumBlockX+1)+zIndex*(2*(nNumBlockX+1)+2))
		{
			pIndex[i] =  (xIndex -1) + zIndex*(nNumBlockX+1);
			pIndex[i+1] = xIndex + (zIndex+1)*(nNumBlockX+1);

			// 段が変わる
			i += 2;
			zIndex++;
			xIndex = 0;
		}

		// 下の点
		if(i % 2 == 0)
		{
			pIndex[i] = xIndex + (zIndex+1)*(nNumBlockX+1);

		}
		// 上の点
		else
		{
			pIndex[i] = xIndex + zIndex*(nNumBlockX+1);
			xIndex++;
		}
	}

	m_pD3DIndexBuff->Unlock();	//アンロック



}

CSceneField*	CSceneField::Create(CRenderer *pRenderer,int nNumBlockX, int nNumBlockZ, float fSizeBlockX, float fSizeBlockZ,D3DXVECTOR3 Pos,D3DXVECTOR3 Rot,char* filename,OBJTYPE Type)
{
	CSceneField* pSceneField=NULL;
	pSceneField=new CSceneField(3,Type);
	pSceneField->Init(pRenderer,nNumBlockX,nNumBlockZ,fSizeBlockX,fSizeBlockZ,Pos,Rot,filename,Type);
	return pSceneField;
}   


BOOL HitCheckTri (D3DXVECTOR3 P1,D3DXVECTOR3 P2,D3DXVECTOR3 P3,D3DXVECTOR3 Pos)
{
	D3DXVECTOR3 Vec1=D3DXVECTOR3(0.0f,0.0f,0.0f),Vec2=D3DXVECTOR3(0.0f,0.0f,0.0f);
	Vec1=P1-P2;
	Vec2=P1-Pos;
	if(Vec1.z*Vec2.x-Vec1.x*Vec2.z>=0)
	{
		Vec1=P2-P3;
		Vec2=P2-Pos;
		if(Vec1.z*Vec2.x-Vec1.x*Vec2.z>=0)
		{
			Vec1=P3-P1;
			Vec2=P3-Pos;
			if(Vec1.z*Vec2.x-Vec1.x*Vec2.z>=0)
			{
				return true;
			}
		}
	}
	
	
	return false;
}


float GetHeightPolygon(const D3DXVECTOR3& P0,
						const D3DXVECTOR3& P1,
						const D3DXVECTOR3& P2,
						const D3DXVECTOR3& Pos,
						D3DXVECTOR3*	pNormal)
{
	if(pNormal->y==0.0f)
	{
		return 0.0f;
	}
	return (P0.y-((pNormal->x*(Pos.x-P0.x))+(pNormal->z*(Pos.z-P0.z)))/pNormal->y);
}

float CSceneField::GetHeight(D3DXVECTOR3 Pos)
{
	int i=0,j=0;
	int num=0;
	int NorCnt=0;
	j=((Pos.x+(((float)m_nNumBlockX*m_fSizeBlockX)/(float)2))/m_fSizeBlockX)+1;
	i=-(((Pos.z-(((float)m_nNumBlockZ*m_fSizeBlockZ)/(float)2))/m_fSizeBlockZ));
	VERTEX_3D *pVtx;
	//ロック
	m_pD3DVtxBuff->Lock(0,0,(void**)&pVtx,0);

	num= i * (m_nNumBlockX+1) + j;
	NorCnt=i*(m_nNumBlockZ*2)+((j-1)*2);
	if(HitCheckTri(pVtx[num-1].vtx,pVtx[num].vtx,pVtx[num+(m_nNumBlockX+1)].vtx,Pos))
	{
		bHitFlag=true;
		Pos.y=GetHeightPolygon(pVtx[num-1].vtx,pVtx[num].vtx,pVtx[num+(m_nNumBlockX+1)].vtx,Pos,&NormalBuff[NorCnt]);
		
	}
	if(!bHitFlag)
	{
		bHitFlag=HitCheckTri(pVtx[num-1].vtx,pVtx[num+(m_nNumBlockX+1)].vtx,pVtx[num-1+(m_nNumBlockX+1)].vtx,Pos);
		Pos.y=GetHeightPolygon(pVtx[num-1].vtx,pVtx[num+(m_nNumBlockX+1)].vtx,pVtx[num-1+(m_nNumBlockX+1)].vtx,Pos,&NormalBuff[NorCnt+1]);
	}
	bHitFlag=false;
	m_pD3DVtxBuff->Unlock();
	return Pos.y;
}